powershell -NoProfile -ExecutionPolicy Bypass -Command "Push-Location 'E:\src\github.com\offbyonestudios\edn'; $ErrorActionPreference='Stop'; $pat='\\build\\|\\x64\\'; $codeCount=(Get-ChildItem -Recurse -File -Include *.c,*.cc,*.cpp,*.cxx,*.h,*.hh,*.hpp,*.hxx,*.inl,*.ipp,*.tpp | Where-Object FullName -notmatch $pat | Measure-Object).Count; $codeLoc=(Get-ChildItem -Recurse -File -Include *.c,*.cc,*.cpp,*.cxx,*.h,*.hh,*.hpp,*.hxx,*.inl,*.ipp,*.tpp | Where-Object FullName -notmatch $pat | Get-Content -ErrorAction SilentlyContinue | Measure-Object -Line).Lines; Write-Output ('CODE_FILES=' + $codeCount); Write-Output ('CODE_LOC=' + $codeLoc); $cmakeCount=(Get-ChildItem -Recurse -File -Include *.cmake | Where-Object FullName -notmatch $pat | Measure-Object).Count + (Get-ChildItem -Recurse -File -Filter CMakeLists.txt | Where-Object FullName -notmatch $pat | Measure-Object).Count; $cmakeLoc=((Get-ChildItem -Recurse -File -Include *.cmake | Where-Object FullName -notmatch $pat | Get-Content -ErrorAction SilentlyContinue | Measure-Object -Line).Lines + (Get-ChildItem -Recurse -File -Filter CMakeLists.txt | Where-Object FullName -notmatch $pat | Get-Content -ErrorAction SilentlyContinue | Measure-Object -Line).Lines); Write-Output ('CMAKE_FILES=' + $cmakeCount); Write-Output ('CMAKE_LOC=' + $cmakeLoc); $testCount=(Get-ChildItem -Recurse -File -Include *test*.cpp,*tests*.cpp | Where-Object FullName -notmatch $pat | Measure-Object).Count; $testLoc=(Get-ChildItem -Recurse -File -Include *test*.cpp,*tests*.cpp | Where-Object FullName -notmatch $pat | Get-Content -ErrorAction SilentlyContinue | Measure-Object -Line).Lines; Write-Output ('TEST_FILES=' + $testCount); Write-Output ('TEST_LOC=' + $testLoc); $mdCount=(Get-ChildItem -Recurse -File -Include *.md | Where-Object FullName -notmatch $pat | Measure-Object).Count; $mdLoc=(Get-ChildItem -Recurse -File -Include *.md | Where-Object FullName -notmatch $pat | Get-Content -ErrorAction SilentlyContinue | Measure-Object -Line).Lines; Write-Output ('MD_FILES=' + $mdCount); Write-Output ('MD_LOC=' + $mdLoc); Pop-Location"